def make_id(name_en):
    slug = name_en.lower().strip()
    slug = slug.replace('&', 'and').replace("'", '').replace(''', '').replace(''', '')
    slug = slug.replace(',', '').replace('.', '').replace('(', '').replace(')', '')
    slug = slug.replace(':', '').replace('"', '').replace("é", "e").replace("ü", "u").replace("ö", "o")
    slug = slug.replace("à", "a").replace("ô", "o").replace("ê", "e").replace("î", "i")
    slug = ''.join(c if c.isalnum() or c == ' ' or c == '-' else '' for c in slug)
    slug = '-'.join(slug.split())
    return slug

def gen_popular_times(cat_type="restaurant"):
    days = ["saturday", "sunday", "monday", "tuesday", "wednesday", "thursday", "friday"]
    result = {}
    for day in days:
        hours = []
        weekend = day in ["thursday", "friday"]
        for h in range(24):
            if 0 <= h <= 5:
                val = random.randint(3, 15)
            elif 6 <= h <= 8:
                if cat_type in ["cafe", "restaurant"]:
                    val = random.randint(15, 40)
                else:
                    val = random.randint(5, 15)
            elif 9 <= h <= 11:
                if cat_type == "cafe":
                    val = random.randint(40, 70)
                elif cat_type in ["mall", "malls", "shopping"]:
                    val = random.randint(25, 50)
                else:
                    val = random.randint(15, 40)
            elif 12 <= h <= 14:
                if cat_type == "restaurant":
                    val = random.randint(45, 80)
                elif cat_type == "cafe":
                    val = random.randint(35, 60)
                else:
                    val = random.randint(25, 55)
            elif 15 <= h <= 16:
                val = random.randint(20, 50)
            elif 17 <= h <= 22:
                base_val = random.randint(55, 90)
                if weekend:
                    base_val = min(95, base_val + random.randint(5, 15))
                val = base_val
            else:
                val = random.randint(15, 40)
            hours.append(val)
        result[day] = hours
    return result

