def make_place(name_ar, name_en, cat_en, neighborhood, neighborhood_en, desc_ar, 
               rating, reviews, price, trending, is_new, review_quote, 
               pros, cons, best_time, avg_spend, lat, lng, audience, 
               is_free, peak_hours, best_visit, district):
    pid = make_id(name_en)
    if pid in existing_ids or pid in new_ids:
        # Add suffix to make unique
        pid = pid + "-" + str(random.randint(2, 99))
    
    cat, cat_ar, cat_en_val = CAT_MAP[cat_en]
    
    return {
        "id": pid,
        "name_ar": name_ar,
        "name_en": name_en,
        "category": cat,
        "category_ar": cat_ar,
        "category_en": cat_en_val,
        "neighborhood": neighborhood,
        "neighborhood_en": neighborhood_en,
        "description_ar": desc_ar,
        "google_rating": rating,
        "review_count": reviews,
        "price_level": price,
        "trending": trending,
        "is_new": is_new,
        "review_quote_ar": review_quote,
        "review_quote": review_quote,
        "pros_ar": pros,
        "pros": pros,
        "cons_ar": cons,
        "cons": cons,
        "best_time": best_time,
        "avg_spend": avg_spend,
        "google_maps_url": f"https://maps.google.com/?q={name_en.replace(' ', '+')}+Riyadh",
        "image_placeholder": f"{pid}.jpg",
        "image_url": random.choice(IMAGES.get(cat_en, IMAGES["restaurant"])),
        "lat": lat,
        "lng": lng,
        "audience": audience,
        "is_free": is_free,
        "popular_times": gen_popular_times(cat_en),
        "peak_hours": peak_hours,
        "best_visit_time": best_visit,
        "district": district
    }

new_ids = set()
new_places = []

def add(name_ar, name_en, cat_en, neighborhood, neighborhood_en, desc_ar,
        rating, reviews, price, trending, is_new, review_quote,
        pros, cons, best_time, avg_spend, lat, lng, audience,
        is_free, peak_hours, best_visit, district):
    p = make_place(name_ar, name_en, cat_en, neighborhood, neighborhood_en, desc_ar,
                   rating, reviews, price, trending, is_new, review_quote,
                   pros, cons, best_time, avg_spend, lat, lng, audience,
                   is_free, peak_hours, best_visit, district)
    if p["id"] not in existing_ids and p["id"] not in new_ids:
        new_ids.add(p["id"])
        new_places.append(p)
    else:
        print(f"  SKIP duplicate: {p['id']}")

